    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        String selecao = comboSelecao.getSelectedItem().toString();
        if(selecao.equalsIgnoreCase("listaCondicional")){
            ListaCondicional listaCondiconal = new ListaCondicional();
            listaCondiconal.setVisible(true);
            setVisible(false);
        }
        else if(selecao.equalsIgnoreCase("listaFor")){
            ListaFor listaFor = new ListaFor();
            listaFor.setVisible(true);
            setVisible(false);
        }
        else if(selecao.equalsIgnoreCase("listaLooping")){
            ListaLooping listaLooping = new ListaLooping();
            listaLooping.setVisible(true);
            setVisible(false);
        }
        else if(selecao.equalsIgnoreCase("listaOperadores")){
            ListaOperadores listaOperadores = new ListaOperadores();
            listaOperadores.setVisible(true);
            setVisible(false);
        }
        else if(selecao.equalsIgnoreCase("listaString")){
            ListaString listaString = new ListaString();
            listaString.setVisible(true);
            setVisible(false);
        }
        else if(selecao.equalsIgnoreCase("listaWhile")){
            ListaWhile listaWhile = new ListaWhile();
            listaWhile.setVisible(true);
            setVisible(false);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

